public static void main(String[]args){
    Map<String, String> params = com.google.common.collect.Maps.newHashMap();

    params.put("", "");
    params.put("", "");

    String content = new DoorduBaseTestCase.Builder()
            .map(params)
            .path("")
            .post();

    System.out.println(content);
}

try {
            ResultData<String> resultData = $CURR_LOCATION$

            if (resultData == null || !ResultCode.RESPONSE_SUCCESS.equals(resultData.getCode())) {
                return error(resultData.getCode(), ResultCode.errmap.get(resultData.getCode()),
                        resultData.getDesc());
            }
            return ok(resultData.getData(), "操作成功");
        } catch (BusinessException ex) {
            logger.error("", ex);

            return error(ResultCode.ERROR_OPERATE, ResultCode.errmap.get(ResultCode.ERROR_OPERATE),
                    ex.getMessage());
        } catch (Exception ex) {
            logger.error("", ex);

            return defaultError(ex);
        }

String errorMsg = "$CURR_LOCATION$";

resultData.setCode(ResultCode.RESPONSE_INTRANET_ERROR);
resultData.setMsg(errorMsg);
resultData.setDesc(errorMsg);

return resultData;

String succ = "$CURR_LOCATION$";

resultData.setCode(ResultCode.RESPONSE_SUCCESS);
resultData.setMsg(succ);
resultData.setDesc(succ);

return resultData;

/**
 *@Description: 重构处理异常提示信息返回
 *@Param:
 *@return:
 *@Author: huaiao
 *@date: $CURR_DATE$
 */
private ResultData<String> resultDataComm(String errorMsg) {
    ResultData<String> resultData = new ResultData<>();

    resultData.setCode(ResultCode.RESPONSE_INTRANET_ERROR);
    resultData.setMsg(errorMsg);
    resultData.setDesc(errorMsg);

    return resultData;
}
